import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Modal from "react-bootstrap/Modal";

import { IoMdAddCircleOutline } from "react-icons/io";
import { Link } from "react-router-dom";
function AddWorkExperience() {
  const arr = [
    {value: '', text: '--Select --'},
    {value: 'apple', text: 'Apple ðŸ'},
    {value: 'banana', text: 'Banana ðŸŒ'},
    {value: 'kiwi', text: 'Kiwi ðŸ¥'},
  ];
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Button variant="light" onClick={handleShow}>
        <IoMdAddCircleOutline />
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Add Previous Work Experience</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Company Name</Form.Label>
              <Form.Select size="lg">
              {arr.map((option, index) => (
          <option key={index} value={option.value}>
            {option.text}
          </option>
        ))}
              </Form.Select>
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Certificate Link</Form.Label>
              <Form.Control placeholder="Enter url" autoFocus />
            </Form.Group>
          </Form>
          <center>
            <Link to="/test">or take a test</Link>
          </center>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Submit
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default AddWorkExperience;
